import React, { useEffect, useState } from "react";
import Meta from "antd/es/card/Meta";
import { SelectedCardWrapper,SelectedImage,SelectedInfo ,ShipContent,ShipTextContent} from "./SelectedCard.styled";
import { Button } from "antd";
import ShipHeader from "./ShipHeader";
import Card from "antd/es/card/Card";
import { useLocation } from "react-router-dom";
import "../SelectedCard/selectedCard.css"
import { getShips } from "../../service/api";
const text =`A ship is a means of transportation consisting
 of two wheels, a frame, pedals, and handlebars. 
 It operates through human power generated by pedaling. 
 Ships are a popular mode of physical activity and transportation, 
ideal for various terrains and purposes, from urban commuting to recreational riding.`

const SelectedCard =()=>{
  const [ships,setShips ] = useState([]);
  useEffect(()=>{
    getShips().then(ships =>{
        setShips(ships)
    })
  },[])

    const location= useLocation()
    const shipId = location.pathname.split('/')[2]

    const shipData = ships.find(b => b.id === Number(shipId) )
    if (!shipData) {

      return <div>Loading...</div>;
    }

    return(
       <SelectedCardWrapper>

           <ShipHeader />
           <ShipContent>
                <Card
                    hoverable 
                    cover = {
                        <img style={{width:400, height: 300}} src = {shipData.image}/>
                    }
                    style={{width:400,height:400}}
                    > 
                      <h2 style={{textAlign:'center'}}>
                        {shipData.title}
                      </h2>
              </Card>
              <div>
                    <div className="sel_item_description">
                      <p>{text}</p>
                    </div>
                    <div className="sel_item_price" >
                         <h2>Price: {shipData.price} $ </h2>
                        <Button className="buy">Buy Now</Button>
                    </div>
              </div>
            </ShipContent>
      </SelectedCardWrapper>

    )
}

export default SelectedCard
