import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ShipList = () => {
  const [ships, setShips] = useState([]);
  const [newShip, setNewShip] = useState({ title: '', type: '', price: 0, imageUrl: '', text: '' });

  useEffect(() => {
    axios.get('/api/ships')
      .then(response => setShips(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('image', e.target.files[0]);

    try {
      const response = await axios.post('/api/upload', formData);
      setNewShip({ ...newShip, imageUrl: response.data.imageUrl });
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddShip = async () => {
    try {
      await axios.post('/api/ships', newShip);
      const updatedShips = await axios.get('/api/ships');
      setShips(updatedShips.data);
      setNewShip({ title: '', type: '', price: 0, imageUrl: '', text: '' });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Список кораблів</h2>
      <ul>
        {ships.map(ship => (
          <li key={ship.id}>{ship. title} - {ship.type} - ${ship.price}</li>
        ))}
      </ul>
      <input type="file" onChange={handleImageUpload} />
      <button onClick={handleAddShip}>Додати корабель</button>
    </div>
  );
};

export default ShipList;
